const itemNotFound = [];
const priceNotMatch = [];
const inventoryNotEnough = [];

const config = $('Configurations').first().json;
const collections = $('Get Webflow CMS Details').all();

function parseSlug(str) {
  return str
    .split(',')
    .map((slug) => slug.trim())
    .filter((slug) => slug !== '');
}
const skipPriceValidateSlugs = parseSlug(config.skipPriceValidate || '');
const skipInventoryValidateSlugs = parseSlug(
  config.skipInventoryValidate || ''
);

const cartItems = $('Foxy Pre-Checkout Webhook').first().json.body._embedded[
  'fx:items'
];
for (const [index, cartItem] of cartItems.entries()) {
  const itemSlug = cartItem._embedded['fx:item_options']?.find(
    (option) => option.name.toLowerCase() === 'slug'
  )?.value;
  if (!itemSlug) {
    itemNotFound.push(cartItem.name);
    continue;
  }

  const shouldSkipPriceValidate = skipPriceValidateSlugs.includes(itemSlug);
  const shouldSkipInventoryValidate =
    skipInventoryValidateSlugs.includes(itemSlug);

  if (shouldSkipPriceValidate && shouldSkipInventoryValidate) {
    continue;
  }

  const wfItem = $('Find Matching Webflow CMS Items').all()[index].json;
  const wfItemData = wfItem.items[0]?.fieldData;

  if (wfItem.pagination.total !== 1 || wfItemData.slug !== itemSlug) {
    itemNotFound.push(`${cartItem.name} (${itemSlug})`);
    continue;
  }

  const cmsFields = collections.find(
    (collection) =>
      collection.json.id ===
        cartItem._embedded['fx:item_options']?.find(
          (option) => option.name.toLowerCase() === 'collection_id'
        )?.value || config.collectionId
  )?.json.fields;
  if (!cmsFields) {
    return {
      ok: false,
      details: 'Invalid collection ID',
    };
  }

  if (config.validatePrice && !shouldSkipPriceValidate) {
    const priceFieldSlug = cmsFields.find(
      (field) => field.displayName === (config.priceField.trim() || 'Price')
    )?.slug;
    if (!priceFieldSlug) {
      return {
        ok: false,
        details: `Price field "${config.priceField}" does not exist`,
      };
    }
    if (wfItemData[priceFieldSlug] !== cartItem.price) {
      priceNotMatch.push(cartItem.name);
    }
  }

  if (config.validateInventory && !shouldSkipInventoryValidate) {
    const inventoryFieldSlug = cmsFields.find(
      (field) =>
        field.displayName === (config.inventoryField.trim() || 'Inventory')
    )?.slug;
    if (!inventoryFieldSlug) {
      return {
        ok: false,
        details: `Inventory field "${config.inventoryField}" does not exist`,
      };
    }
    if (wfItemData[inventoryFieldSlug] < cartItem.quantity) {
      inventoryNotEnough.push(cartItem.name);
    }
  }
}

if (
  itemNotFound.length > 0 ||
  priceNotMatch.length > 0 ||
  inventoryNotEnough.length > 0
) {
  return {
    ok: false,
    details:
      (itemNotFound.length > 0
        ? `${
            config.itemErrorMsg || 'Cannot find item(s) in Webflow'
          }: ${itemNotFound.join(', ')}. `
        : '') +
      (priceNotMatch.length > 0
        ? `${
            config.priceErrorMsg || 'Price does not match for item(s)'
          }: ${priceNotMatch.join(', ')}. `
        : '') +
      (inventoryNotEnough.length > 0
        ? `${
            config.inventoryErrorMsg || 'Insufficient inventory for item(s)'
          }: ${inventoryNotEnough.join(', ')}.`
        : ''),
  };
}

return {
  ok: true,
};

const collectionIds = [
  ...new Set(
    $('Foxy Pre-Checkout Webhook')
      .first()
      .json.body._embedded['fx:items'].map(
        (item) =>
          item._embedded['fx:item_options']?.find(
            (option) => option.name === 'collection_id'
          )?.value || ''
      )
  ),
].filter((id) => id !== '');

return collectionIds.length > 0
  ? collectionIds.map((id) => ({
      collectionId: id,
    }))
  : [{ collectionId: $('Configurations').first().json.collectionId }];
